#####   rpart_classification.R ####library(rpart)path = paste0(getwd(), "../datasets")setwd(path)Args <- commandArgs(trailingOnly = T)#Gets train and test data fromtrain_data <- read.csv(Args[1])test_data <- read.csv(Args[2])##Todo: get data from ticket for hyperparams## hyperparams = rpart.control(minsplit = ?, cp = ?, daxdepth = ?)#predictor <- Args[3]#Create a formula using all the columsn as predictorsformula = paste0(predictor, "~", ".")#Trains the model and saves it to the model variable.model <- rpart(formula = formula,                method = anova,                #fitControl = hyperparams,                data = train_data              )#This will print a summary of the model, do we want to save information from this to the ticket?print(summary(model))